End Sub
Private Sub flex_MouseEnterCell(・・・
    ' セル内にマウスポインタが入ればスタイルを設定
    flex.SetCellStyle(e.Row, e.Col, flex.Styles("track"))
End Sub
Private Sub flex_MouseLeaveCell(・・・
